        matriculaField.setText(nave.getMatricula());
        estadoField.setText(nave.getEstado());
        if (modeloCombo != null && nave.getModelo() != null) {
            Modelo modelo = modelos.stream()
                    .filter(m -> m.getId().equals(nave.getModelo().getId()))
